import React from "react";
import { useState, createContext } from "react";
const payloadData = createContext();
function context({ chilren }) {
  const [value, setValue] = useState();
  const handleSubmit = (payload) => {
    setValue(payload);
  };
  const objectData = {
    value,
    handleSubmit,
  };
  return (
    <div>
      <payload.Provider value={objectData}>{chilren}</payload.Provider>
    </div>
  );
}

export { payloadData, context };
