import React, { useState, useRef, useEffect, Suspense } from "react";
// import Content from "components/content";

const Content = React.lazy(() => import("./components/content"));
import Video from "components/video";
import Pagination from "components/pagination";
import Post from "components/post";
import Axios from "axios";
import * as S from "./styled";
const Home = () => {
  const videoRef = useRef();
  useEffect(() => {
    console.log(videoRef);
  });
  const handlePlay = () => {
    videoRef.current.play();
  };
  const handlePause = () => {
    videoRef.current.pause();
  };
  //

  const [posts, setPosts] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [loading, setLoading] = useState(false);
  const [postsPerPage, setPostsPerPage] = useState(5);

  useEffect(() => {
    const handleRenderPosts = async () => {
      setLoading(true);
      const fetchPosts = await Axios.get(
        "https://jsonplaceholder.typicode.com/posts"
      );

      setPosts(fetchPosts.data);

      setLoading(false);
    };
    handleRenderPosts();
  }, []);

  const indexLastPost = currentPage * postsPerPage;
  const indexFirstPost = indexLastPost - postsPerPage;

  const currentPosts = posts.slice(indexFirstPost, indexLastPost);

  const paginate = (pageNumber) => {
    setCurrentPage(pageNumber);
  };
  return (
    <>
      <div className="container">
        <h1>Home ne</h1>
        {/* <Video ref={videoRef} />

        <S.Button onClick={handlePlay}>play</S.Button>
        <S.Button onClick={handlePause}>pause</S.Button> */}
        <Suspense fallback={<div>Loading...</div>}>
          <Content />
        </Suspense>

        <Post posts={currentPosts} loading={loading} />
        <Pagination
          postsPerPage={postsPerPage}
          totalPosts={posts.length}
          paginate={paginate}
        />
      </div>
    </>
  );
};
export default Home;
