import React, { useState, useRef, useEffect } from "react";
import Content from "components/content";
import Video from "components/video";
import Pagination from "components/pagination";
import Axios from "axios";
import * as S from "./styled";
const Home = () => {
  const videoRef = useRef();
  useEffect(() => {
    console.log(videoRef);
  });
  const handlePlay = () => {
    videoRef.current.play();
  };
  const handlePause = () => {
    videoRef.current.pause();
  };
  //

  const [posts, setPosts] = useState([]);
  const [currentPosts, setCurrentPosts] = useState(2);
  const [postNumber, setPostNumber] = useState();

  useEffect(() => {
    const handleRenderPosts = async () => {
      const fetchPosts = await Axios.get(
        "https://jsonplaceholder.typicode.com/posts"
      );

      setPosts(fetchPosts.data);
    };
    handleRenderPosts();
  });

  return (
    <>
      <div className="container">
        <h1>Home ne</h1>
        <Video ref={videoRef} />

        <S.Button onClick={handlePlay}>play</S.Button>
        <S.Button onClick={handlePause}>pause</S.Button>

        {/* <Content /> */}

        <div className="pagination">
          <Pagination posts={posts} />
        </div>
      </div>
    </>
  );
};
export default Home;
