import React, { useState, useRef, useEffect } from "react";
import Content from "components/content";
import Video from "components/video";
import Pagination from "components/pagination";
import Axios from "axios";
import * as S from "./styled";
const Home = () => {
  const videoRef = useRef();
  useEffect(() => {
    console.log(videoRef);
  });
  const handlePlay = () => {
    videoRef.current.play();
  };
  const handlePause = () => {
    videoRef.current.pause();
  };
  //

  const [posts, setPosts] = useState([]);
  const [currentPosts, setCurrentPosts] = useState(1);
  const [loading, setLoading] = useState(false);
  const [postsPerPage, setPostsPerPage] = useState(4);

  useEffect(() => {
    const handleRenderPosts = async () => {
      setLoading(true);
      const fetchPosts = await Axios.get(
        "https://jsonplaceholder.typicode.com/posts"
      );
      const indexLastPosts = currentPosts * postsPerPage;
      const indexFirstPosts = indexLastPosts - postsPerPage;

      const postsCurrent = posts.slice(indexLastPosts, indexFirstPosts);

      setPosts(fetchPosts.data);
      // setCurrentPosts(postsCurrent);
      setLoading(false);
    };
    handleRenderPosts();
  }, []);

  return (
    <>
      <div className="container">
        <h1>Home ne</h1>
        {/* <Video ref={videoRef} />

        <S.Button onClick={handlePlay}>play</S.Button>
        <S.Button onClick={handlePause}>pause</S.Button> */}

        {/* <Content /> */}

        <div className="pagination">
          <Pagination posts={posts} loading={loading} />
        </div>
      </div>
    </>
  );
};
export default Home;
