import styled from "styled-components";

export const WrapPopular = styled.div`
  padding: 20px 0px;
  position: relative;
`;
export const WrapText = styled.div`
  padding: 20px;
  text-align: center;
`;
export const PopularProducts = styled.div`
  padding-top: 30px;
`;
export const ProductItem = styled.div`
  border-radius: ${({ theme }) => theme.borderRadius.md};
  border: 0.1px solid ${({ theme }) => theme.colors.blue2};
  padding: 10px;
  text-align: center;
  margin: 20px;
  &:hover {
    background: ${({ theme }) => theme.colors.blue2};
    color: ${({ theme }) => theme.colors.white1};
    box-shadow: ${({ theme }) => theme.boxShadow.buttonBox};

    & button {
      background: ${({ theme }) => theme.colors.white1};
      color: ${({ theme }) => theme.colors.blue2};
    }
  }

  & img {
    width: 230px;
    height: 200px;
    object-fit: cover;
  }
`;

export const WrapCollections = styled.div`
  padding: 20px 0px;
  display: flex;
  justify-content: space-between;
`;

export const Price = styled.h3``;
export const AddToCart = styled.button`
  padding: 10px 20px;
  background: ${({ theme }) => theme.colors.blue2};
  color: ${({ theme }) => theme.colors.white1};
`;

export const CartIconFloat = styled.div`
  width: 50px;
  height: 50px;
  border-radius: ${({ theme }) => theme.borderRadius.xxl};
  background: ${({ theme }) => theme.colors.blue2};
  position: fixed;
  top: 10px;
  left: 20px;
  display: flex;
  justify-content: center;
  align-items: center;
`;

export const SeeAllMenu = styled.div`
  padding: 60px 0px;
  position: relative;
  display: flex;
  justify-content: center;
`;
