import React, { useState } from "react";
import Content from "components/content";

const courses = [
  {
    id: 1,
    name: "HTML-CSS",
  },
  {
    id: 2,
    name: "Javascript",
  },
  {
    id: 3,
    name: "Reactjs",
  },
];
const Home = () => {
  const [checked, setChecked] = useState();

  //   const handleSubmit = () => {
  //     console.log({ id: checked });
  //   };

  return (
    <>
      <h1>Home ne</h1>
      <div>
        {courses.map((course) => {
          <div key={course.id}>
            <input
              type="checkbox"
              checked={checked === course.id}
              onChange={() => setChecked(course.id)}
            />
            {course.name}
          </div>;
        })}
        {/* <Content /> */}
      </div>
    </>
  );
};
export default Home;
