const colors = {
  white1: "#FFFFFF",
  grey1: "#EAEAEA",
  grey2: "#7d7777",
  greenSky1: "#7CD2D7",
  greenSky2: "#B1D9DB",
  black: "#000000",
  red: "#ff0000",
};
const fontSize = {
  base: "16px",
  md: "20px",
  xl: "24px",
  large: "30px",
};
const fontWeight = {
  regular: 400,
  medium: 500,
  bold: 600,
  extraBold: 700,
};
const zIndex = {
  base: 1,
  dropdown: 10,
  backdrop: 50,
  modal: 100,
};
const boxShadow = {
  buttonBox:
    "0 8px 8px 0 rgba(103, 151, 255, .11), 0 12px 18px 0 rgba(103, 151, 255, .11)",
  layoutBox: "rgba(0, 0, 0, 0.2) 0px 0px 10px",
};
const borderRadius = {
  base: "9px",
  md: "20px",
};
export default {
  colors,
  fontSize,
  fontWeight,
  zIndex,
  boxShadow,
  borderRadius,
};
