import { css } from "styled-components";
import fontSegoeUi from "./fonts/Segoe-Ui/segoeui.woff";
import fontSegoeUi2 from "./fonts/Segoe-Ui/segoeuib.ttf";
import { createGlobalStyle } from "styled-components";
const fontFace = css`
  @font-face {
    font-family: "Segoe-Ui";
    src: url(${fontSegoeUi}) format("woff"),
    src:url(${fontSegoeUi2}) format("ttf");
  }
  html {
    font-family: "Segoe-Ui";
  }
`;

const resetFont = createGlobalStyle`
${fontFace}
`;
export default resetFont;
