import i18n from "i18next";
import LanguageDetector from "i18next-browser-languagedetector";
import { initReactI18next } from "react-i18next";
import Backend from "i18next-xhr-backend";
import vi from "./Vi/vietnamese.json";
import en from "./Eng/english.json";
import ko from "./Ko/korean.json";

i18n
  .use(Backend)
  .use(LanguageDetector)
  .use(initReactI18next)
  .init({
    resources: {
      vietnamese: {
        common: vi,
      },
      ennglish: {
        common: en,
      },
      korean: {
        common: ko,
      },
    },
    fallbackLng: "en",
    debug: true,

    interpolation: {
      escapeValue: false,
    },
  });
export default i18n;
