import styled from "styled-components";

export const WrapDishesList = styled.div`
  padding: 20px 0px;
`;
export const WrapText = styled.div`
  text-align: center;
`;
export const WrapSlide = styled.div`
  padding: 40px 0px;
`;
export const SlideItem = styled.div`
  padding: 30px;
  margin-right: 15px;
  border-radius: ${({ theme }) => theme.borderRadius.md};
  background: ${({ theme }) => theme.colors.greenSky3};
  text-aligns: center;
  text-transform: uppercase;
  transition: background 0.3s ease;

  &:hover {
    background: ${({ theme }) => theme.colors.blue2};
    color: ${({ theme }) => theme.colors.white1};
    box-shadow: ${({ theme }) => theme.boxShadow.buttonBox};
  }
`;

export const Circle = styled.div`
  width: 150px;
  height: 150px;
  display: flex;
  justify-content: center;
  align-items: center;
  background: ${({ theme }) => theme.colors.white1};
  border-radius: ${({ theme }) => theme.borderRadius.xxl};
`;

export const ButtonSlide = styled.div`
  display: flex;
  justify-content: flex-end;
  margin-top: 30px;
`;

export const BtnNext = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  border: none;
  out-line: none;
`;

export const BtnPrev = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  border: none;
  out-line: none;
`;
