import styled, { keyframes } from "styled-components";

export const WrapPopular = styled.div`
  padding: 20px 0px;
  position: relative;
`;
export const WrapText = styled.div`
  padding: 20px;
  text-align: center;
`;
export const PopularProducts = styled.div`
  padding-top: 30px;
`;
export const ProductItem = styled.div`
  border-radius: ${({ theme }) => theme.borderRadius.md};
  border: 0.1px solid ${({ theme }) => theme.colors.blue2};
  padding: 10px;
  text-align: center;
  margin: 20px;
  transition: all 0.3s ease;
  &:hover {
    background: ${({ theme }) => theme.colors.blue2};
    color: ${({ theme }) => theme.colors.white1};
    box-shadow: ${({ theme }) => theme.boxShadow.buttonBox};

    ${
      "" /* & button {
      background: ${({ theme }) => theme.colors.white1};
      color: ${({ theme }) => theme.colors.blue2};
    } */
    }
  }

  & img {
    width: 230px;
    height: 200px;
    object-fit: cover;
  }
`;

export const WrapCollections = styled.div`
  padding: 20px 0px;
  display: flex;
  justify-content: space-between;
`;
export const slice = keyframes`
  0% {
    transform: scale(1);
    opacity: 1;
  }
  50% {
    transform: scale(10);
    opacity: 0.375;
  }
  100% {
    transform: scale(35);
    opacity: 0;
  }
`;

export const Price = styled.h3``;

export const AddToCart = styled.button`
  border-radius: 4px;
  border: none;
  margin: 8px;
  padding: 14px 24px;
  background: #1976d2;
  color: #fff;
  overflow: hidden;
  position: relative;
  cursor: pointer;
  ${
    "" /* position: relative;
  padding: 10px 20px;
  background: ${({ theme }) => theme.colors.blue2};
  color: ${({ theme }) => theme.colors.white1};
  border-radius: ${({ theme }) => theme.borderRadius.base};
  backdrop-filter: blur(8px); */
  }
  ${
    "" /* &:hover {
    box-shadow: ${({ theme }) => theme.boxShadow.layoutBox};
  } */
  }

  & .bubble-pointer {
    width: 20px;
    height: 20px;
    position: absolute;
    background: #63a4ff;
    display: block;
    content: "";
    border-radius: 9999px;
    opacity: 1;
    animation: 0.9s ease 1 forwards ${slice};
  }
`;

export const CartIconFloat = styled.div`
  width: 50px;
  height: 50px;
  border-radius: ${({ theme }) => theme.borderRadius.xxl};
  background: ${({ theme }) => theme.colors.blue2};
  position: fixed;
  top: 10px;
  left: 20px;
  display: flex;
  justify-content: center;
  align-items: center;
`;

export const SeeAllMenu = styled.div`
  padding: 60px 0px;
  position: relative;
  display: flex;
  justify-content: center;
`;
