import React, { useState } from "react";
import * as S from "./styled";
import { Link } from "react-router-dom";
import { useForm } from "react-hook-form";
import { schemaValidateSignUp } from "./constants";
import { yupResolver } from "@hookform/resolvers/yup";
import { useTranslation, Trans } from "react-i18next";

import Input from "components/Form/InputField";
import InputPassWord from "components/Form/PasswordField";
import { Heading2 } from "components/common/Text";
import Button from "components/Button";
import AppLayout from "components/AppLayout";

import IconFb from "images/logo-facebook.png";
import IconGg from "images/logo-google.png";
import ImageFloat from "images/image-sign-up.png";
const Register = () => {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(schemaValidateSignUp),
  });
  const onSubmit = (data) => console.log("data", data);

  const { t, i18n } = useTranslation();
  const changeLanguage = (lng) => {
    i18n.changeLanguage(lng);
  };
  return (
    <>
      <AppLayout>
        <S.Background>
          <div className="row">
            <div className="col-md-5 d-none d-md-block">
              <S.WrapperFloat>
                <div style={{ height: "100vh" }}></div>
                <S.ImgFloat src={ImageFloat} alt="" />
              </S.WrapperFloat>
            </div>
            <div className="col-md-7">
              <S.Wrapper>
                <div className="row">
                  <div className="col-md-10">
                    <Button onClick={changeLanguage("vn")}>Vn</Button>
                    <Button onClick={changeLanguage("en")}>En</Button>

                    <h2>{t("i18n")}</h2>
                    <Trans>Hello</Trans>
                    <Heading2
                      padding="20px 0px 20px 0px"
                      margin="10px 0px"
                      size="40px"
                    >
                      Sign Up{" "}
                    </Heading2>
                    <div className="row">
                      <div className="col-md-6">
                        <Button $icon={IconGg} isBorder>
                          Sign up with Google
                        </Button>
                      </div>
                      <div className="col-md-6">
                        <Button $icon={IconFb} isBorder>
                          Sign up with Facebook
                        </Button>
                      </div>
                    </div>
                    <S.BoxCenter>
                      <span>
                        {"-"} or {"-"}
                      </span>
                    </S.BoxCenter>

                    <form onSubmit={handleSubmit(onSubmit)}>
                      <div className="form-group pt-3">
                        <Input
                          type="text"
                          placeholder="Full Name"
                          name="name"
                          register={register}
                        />
                        {errors.name && <p>{errors.name.message}</p>}
                      </div>
                      <div className="form-group pt-3">
                        <Input
                          type="email"
                          placeholder="Email Address"
                          name="email"
                          register={register}
                        />
                        {errors.email && <p>{errors.email.message}</p>}
                      </div>
                      <div className="form-group pt-3">
                        <InputPassWord
                          placeholder="Password"
                          name="password"
                          register={register}
                        />
                        {errors.password && <p>{errors.password.message}</p>}
                      </div>
                      <div className="form-group pt-5 ">
                        <Button type="submit" isHover isBlue textCenter>
                          Sign Up
                        </Button>
                      </div>
                    </form>
                    <div className="form-group pt-3">
                      <span className="account-text ms-2 me-2">
                        Already have an account ?
                      </span>

                      <Link to="/login">Log in </Link>
                    </div>
                  </div>
                </div>
              </S.Wrapper>
            </div>
          </div>
        </S.Background>
      </AppLayout>
    </>
  );
};
export default Register;
